import React, { useEffect, useState } from 'react'
import axios from 'axios'

const DeleteUserApi = () => {
    const [users, setUsers]= useState([])

    const getUsers = async()=>{
        
    const res = await axios.get("https://jsonplaceholder.typicode.com/users")
    const data = res.data
    setUsers(data)
    console.log(data)

    }

    const handleDelete =(id)=>{
        const updateData = users.filter((elem,idx)=>{
           return idx !== id;
        })
        setUsers(updateData)
    }
    useEffect(() => {
      getUsers()
    
     
    }, [])
    
  return (
    <>
      <div>
       {/* <button onClick={getUsers}>Get Images</button> */}
     {
       users.map((elem, id)=>{
            return <div key={id} style={{display:"flex" , alignItems:"center", margin:"10px 5px"}}>
                <h1>Name: {elem.name}</h1> <button onClick={()=>handleDelete(id)} style={{padding:"10px 15px", margin:"0px 10px"}}>Delete</button>
                </div>
        })
     }   
      </div>
    </>
  )
}

export default DeleteUserApi
