import React, { useEffect, useState } from 'react'
import axios from 'axios'

const DeleteUserApi = () => {
    const [users, setUsers]= useState([])

    const getUsers = async()=>{
        
    const res = await axios.get("https://jsonplaceholder.typicode.com/users")
    const data = res.data
    setUsers(data)
    console.log(data)

    }

    const handleDelete =(id)=>{
        const updateData = users.filter((elem,idx)=>{
           return idx !== id;
        })
        setUsers(updateData)
    }
    useEffect(() => {
      getUsers()
    
     
    }, [])
    
  return (
    <>
      <div>
       {/* <button onClick={getUsers}>Get Images</button> */}
     {
       users.map((elem, id)=>{
            return <div key={id} style={{display:"flex" , alignItems:"center", justifyContent:"center", margin:"10px 5px"}}>
              <div style={{maxWidth:"375",display:"flex", justifyContent:"left", alignItems:"center", marginLeft:"40%"}}>

                <h1 style={{background:"lightgrey", padding:"5px 10px" , width:"100%"}}>Name : {elem.name}</h1> <button onClick={()=>handleDelete(id)} style={{height:"50px", margin:"0px 10px"}}>Delete</button>
              </div>
                </div>
        })
     }   
      </div>
    </>
  )
}

export default DeleteUserApi
