import React from 'react'
import { ToastContainer, toast } from 'react-toastify';

import 'react-toastify/dist/ReactToastify.css';
const ReactToastify = () => {
    const notify =()=>{
        toast("Login Succesful"),{

            position: "top-",
    autoClose: 5000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    progress: undefined,
    theme: "light"
        }
    }
  return (
    <div>
      <button onClick={notify} style={{fontSize:"21px", padding:"10px 20px", margin:"10px", borderRadius:"7px", color:"black", border:"none", background:"grey"}}>Login</button>
      <ToastContainer/>
    </div>
  )
}

export default ReactToastify
