import React, { useEffect, useState } from 'react'
import "./PhotosApi.css"
import axios from 'axios'
import Spinner from '../spinner/Spinner'

const PhotosApi = () => {
  const imgTitle =["Laptop & Tea", "Work", "Pending Work", ""]
  const [inputVal, setInputVal] = useState("")
  const [datas, setDatas] = useState([])
  const [spinner, setSpinner]= useState(false)
  const [description, setDescription] = useState()

const handleChange = (e)=>{
  setInputVal(e.target.value)
}

  const getApiData = async () => {
    const res = await axios.get("https://picsum.photos/v2/list")
    const data = res.data
    setDatas(data)
    setSpinner(true)
    setSpinner(false)
    
    //  console.log(data)
  }

  const searchUpdate = datas.filter((elem)=>{
        return elem.author.toLowerCase().includes(inputVal.toLowerCase())
  })

  useEffect(() => {
    getApiData()


  }, [])


  return (

    <>
    <h1 className='logo'>Image Gallary</h1>
      <input onChange={handleChange} type="text" value={inputVal} placeholder='Search' />
 
 {
  spinner ? <Spinner/> :
    <div className='photosApi'>
            {
        searchUpdate.map((elem, id) => {
          return <div className="box" key={id}>
            <img src={elem.download_url} alt='images' />
            <p>{elem.author}</p>
          </div>

          
        })
      
      }
    </div>
 }
      
    </>
  )
}

export default PhotosApi
