import React, { useEffect, useState } from 'react'
import axios from 'axios'

const ImageApi = () => {
    const [users, setUsers]= useState([])

    const getUsers = async()=>{
        
    const res = await axios.get("https://jsonplaceholder.typicode.com/users")
    const data = res.data
    setUsers(data)
    console.log(data)

    }

    const handleDelete =(idx)=>{
        const updateData = users.filter((id)=>{
           return id !== idx;
        })
        setUsers(updateData)
    }
    useEffect(() => {
      getUsers()
    
     
    }, [])
    
  return (
    <>
      <div>
       <button onClick={getUsers}>Get Images</button>
     {
       users.map((elem, id)=>{
            return <div key={id} style={{display:"flex" , alignItems:"center", margin:"10px 5px"}}>
                <h1>Name:{elem.name}</h1> <button onClick={handleDelete} style={{padding:"10px 15px", margin:"0px 10px"}}>Delete</button>
                </div>
        })
     }   
      </div>
    </>
  )
}

export default ImageApi
