import React, { useState } from 'react'

const Search = () => {
  const initialArray = ["Steve Smith", "Babar Azam", "Maxwell", "Aron Finch", "Joe Root", "Tim David", "Shaheen Afridi", "Naseem Shah", "Amir", "Haris Rauf", "Mitchel Start", "Trent Boult", "Abdullah Shafeeq"]
  const [array, setArray] = useState (initialArray)

  

  const [inputValue, setInputValue] = useState("")

  const handleChange = (e) => {
    setInputValue(e.target.value)
  }
  const updateSearch = array.filter((elem)=>{
    return elem.toLowerCase().includes(inputValue.toLowerCase())
   })
 
   console.log(inputValue)

 

  return (
    <>
      <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
        <h1>Search</h1>
        <input onChange={handleChange} value={inputValue} type="text" style={{ width: "300px", padding: "10px" }} />

        <div style={{ margin: "20px 0px", width: "300px", }}>
          {
            updateSearch.map((elem, id) => {
              return <div elem={elem} style={{ margin: "20px 0px", background: "grey", padding: "10px" }} key={id}> {elem}</div>
            })
          }
        </div>
      </div>
    </>
  )
}

export default Search
